            $("#load-more-link")["hide"]();
            $["ajax"]({
                url: b,
                success: function(a) {
                    var c = $(a)["find"](".grid-posts");
                    c["find"](".index-post")["addClass"]("post-animated post-fadeInUp");
                    $(".grid-posts")["append"](c["html"]());
                    b = $(a)["find"]("#load-more-link")["data"]("load");
